Map::Map() {
    loadMap(definedmap);
}

Map::~Map() {}

void Map::loadMap(int arr[44][80]) {
    for (int row = 0; row < 44; ++row) {
        for (int col = 0; col < 80; ++col) {
            map[col][row] = arr[col][row];
        }
    }
}

void Map::drawMap() {
    int type = 0;
    int x = 0;
    int y = 0;
    for (int row = 0; row < 44; ++row) {
        for (int col = 0; col < 80; ++col) {
            x = col * 24 + 12;
            y = row * 24 + 12;
            type = map[col][row];
            switch (type) {
                case 0:
                    break;
                case 1:
                    StaticLists::gameObjectsList.push_back(new WallPiece(y, x));
                    break;
                case 2:
                    break;
            }
        }
    }
}