Map::Map() {
    for (int row = 0; row < 45; ++row) {
        for (int col = 0; col < 80; ++col) {
            map[row][col] = definedmap[row][col];
        }
    }
    drawMap();
}

Map::~Map() {}

void Map::drawMap() {
    int type = 0;
    int x = 0;
    int y = 0;
    for (int row = 0; row < 45; ++row) {
        for (int col = 0; col < 80; ++col) {
            x = col * 24 + 12;
            y = row * 24 + 12;
            type = map[row][col];
            switch (type) {
                case 0:
                    break;
                case 1:
                    StaticLists::gameObjectsList.push_back(new WallPiece(x, y));
                    break;
                case 2:
                    StaticLists::gameObjectsList.push_back(new Door(x, y));
                    break;
                case 3:
                    StaticLists::gameObjectsList.push_back(new Button(x, y));
                    break;
                case 4:
                    StaticLists::gameObjectsList.push_back(new Box(x, y));
                    break;
                case 8:
                    StaticLists::gameObjectsList.push_back(new Animal(x, y, 1));
                    break;
                case 9:
                    StaticLists::gameObjectsList.push_back(new Animal(x, y, 2));
                    break;
                case 10:
                    StaticLists::gameObjectsList.push_back(new Animal(x, y, 3));
                    break;
                case 11:
                    StaticLists::gameObjectsList.push_back(new Animal(x, y, 4));
                    break;
                default:
                    break;
            }
        }
    }
}