Map::Map() {
    for (int row = 0; row < 45; ++row) {
        for (int col = 0; col < 80; ++col) {
            map[row][col] = definedmap[row][col];
        }
    }
    drawMap();
}

Map::~Map() {}

void Map::drawMap() {
    int type = 0;
    int x = 0;
    int y = 0;
    for (int row = 0; row < 45; ++row) {
        for (int col = 0; col < 80; ++col) {
            x = col * 24 + 12;
            y = row * 24 + 12;
            type = map[row][col];
            switch (type) {
                case 0:
                    break;
                case 1:
                    StaticLists::gameObjectsList.push_back(new WallPiece(x,y));
                    break;
                case 2:
                    break;
                default:
                    break;
            }
        }
    }
}